import Web3 from 'web3';
import mailContractData from './DecentralizedMail.json';

let web3Instance;
let mailContract;

export const initWeb3 = async () => {
    if (web3Instance) return web3Instance;

    if (window.ethereum) {
        web3Instance = new Web3(window.ethereum);
        try {
            await window.ethereum.request({ method: 'eth_requestAccounts' });
        } catch (error) {
            console.error('User denied account access', error);
        }
    } else if (window.web3) {
        web3Instance = new Web3(window.web3.currentProvider);
    } else {
        const provider = new Web3.providers.HttpProvider('https://public-node.testnet.rsk.co');
        web3Instance = new Web3(provider);
    }
    console.log("Web3 initialized:", web3Instance);
    return web3Instance;
};

export const getWeb3 = () => {
    return web3Instance;
};

export const getContract = async () => {
    if (mailContract) return mailContract;

    const web3 = await initWeb3();
    const networkId = '31'; // RSK Testnet network ID
    const deployedNetwork = mailContractData.networks[networkId];
    if (!deployedNetwork) {
        throw new Error('Contract not deployed on the RSK Testnet');
    }
    mailContract = new web3.eth.Contract(
        mailContractData.abi,
        deployedNetwork.address
    );

    console.log("Contract initialized:", mailContract);
    return mailContract;
};
