import React, { useEffect, useState } from 'react';
import { List, ListItem, ListItemText, Typography, Card, CardContent, Box, Button } from '@mui/material';
import { getWeb3, getContract } from '../utils/contract';
import { useNavigate } from 'react-router-dom';
import '../styles/Inbox.css';

const Inbox = () => {
    const [messages, setMessages] = useState([]);
    const [status, setStatus] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMessages = async () => {
            const web3 = await getWeb3();
            const accounts = await web3.eth.getAccounts();
            const contract = await getContract(web3);

            try {
                const userInbox = await contract.methods.getUserInbox(accounts[0]).call();
                setMessages(userInbox);
                setStatus('');
            } catch (error) {
                console.error('Error fetching messages:', error);
                setStatus('Failed to fetch messages');
            }
        };

        fetchMessages();
    }, []);

    const handleDelete = async (messageId) => {
        const web3 = await getWeb3();
        const accounts = await web3.eth.getAccounts();
        const contract = await getContract(web3);

        try {
            await contract.methods.deleteMessage(messageId).send({ from: accounts[0] });
            setMessages(messages.filter(message => message.id !== messageId));
        } catch (error) {
            console.error('Error deleting message:', error);
            setStatus('Failed to delete message');
        }
    };

    const handleViewDetail = (messageId) => {
        navigate(`/message/${messageId}`);
    };

    return (
        <Card className="card" style={{ backgroundColor: '#002366', color: 'white' }}>
            <CardContent>
                <Typography variant="h5" gutterBottom>Inbox</Typography>
                {status && (
                    <Typography variant="body2" color="error" gutterBottom>
                        {status}
                    </Typography>
                )}
                <List>
                    {messages.map((msg, index) => (
                        <ListItem key={index} divider>
                            <ListItemText
                                primary={`From: ${msg.sender}`}
                                secondary={
                                    <>
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            {msg.content}
                                        </Typography>
                                        {msg.imageHash && (
                                            <Box mt={2}>
                                                <img
                                                    src={`https://gateway.pinata.cloud/ipfs/${msg.imageHash}`}
                                                    alt="Attachment"
                                                    style={{ maxWidth: '100%', height: 'auto', marginTop: '10px' }}
                                                />
                                            </Box>
                                        )}
                                    </>
                                }
                                onClick={() => handleViewDetail(msg.id)}
                                style={{ cursor: 'pointer' }}
                            />
                            <Button variant="contained" color="secondary" onClick={() => handleDelete(index)}>
                                Delete
                            </Button>
                        </ListItem>
                    ))}
                </List>
            </CardContent>
        </Card>
    );
};

export default Inbox;
