import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Card, CardContent, Typography, Box } from '@mui/material';
import { getWeb3, getContract } from '../utils/contract';
import '../styles/MessageDetail.css';

const MessageDetail = () => {
    const { messageId } = useParams();
    const [message, setMessage] = useState(null);
    const [status, setStatus] = useState('');

    useEffect(() => {
        const fetchMessage = async () => {
            const web3 = await getWeb3();
            const contract = await getContract(web3);

            try {
                const msg = await contract.methods.getMessageById(messageId).call();
                setMessage(msg);
                setStatus('');
            } catch (error) {
                console.error('Error fetching message:', error);
                setStatus('Failed to fetch message');
            }
        };

        fetchMessage();
    }, [messageId]);

    return (
        <Card className="message-detail-content">
            <CardContent>
                <Typography variant="h5" gutterBottom>Message Detail</Typography>
                {status && (
                    <Typography variant="body2" color="error" gutterBottom>
                        {status}
                    </Typography>
                )}
                {message && (
                    <>
                        <Typography variant="h6">From: {message.sender}</Typography>
                        <Typography variant="h6">To: {message.receiver}</Typography>
                        <Typography variant="body1" paragraph>{message.content}</Typography>
                        {message.imageHash && (
                            <Box className="message-detail-image-container">
                                <img
                                    src={`https://gateway.pinata.cloud/ipfs/${message.imageHash}`}
                                    alt="Attachment"
                                    className="message-detail-image"
                                />
                            </Box>
                        )}
                    </>
                )}
            </CardContent>
        </Card>
    );
};

export default MessageDetail;
