import React from 'react';
import { useParams } from 'react-router-dom';
import { Card, CardContent, Typography, Box } from '@mui/material';
import '../styles/MessageDetail.css';

const MessageDetail = ({ messages }) => {
    const { id } = useParams();
    const message = messages[parseInt(id)];

    if (!message) {
        return (
            <Card className="message-detail-content">
                <CardContent>
                    <Typography variant="h6" color="error">Message not found</Typography>
                </CardContent>
            </Card>
        );
    }

    return (
        <Card className="message-detail-content">
            <CardContent>
                <Typography variant="h5" gutterBottom>
                    From: {message.sender}
                </Typography>
                <Typography variant="body1" paragraph>
                    {message.content}
                </Typography>
                {message.imageHash && (
                    <Box className="message-detail-image-container">
                        <img
                            src={`https://gateway.pinata.cloud/ipfs/${message.imageHash}`}
                            alt="Attachment"
                            className="message-detail-image"
                        />
                    </Box>
                )}
                <Typography variant="caption" color="textSecondary">
                    Sent at: {new Date(message.timestamp * 1000).toLocaleString()}
                </Typography>
            </CardContent>
        </Card>
    );
};

export default MessageDetail;
