import React, { useState } from 'react';
import { TextField, Button, Typography, Box } from '@mui/material';
import { getWeb3, getContract } from '../utils/contract';
import axios from 'axios';

const SendMessage = () => {
    const [recipient, setRecipient] = useState('');
    const [message, setMessage] = useState('');
    const [file, setFile] = useState(null);
    const [status, setStatus] = useState('');

    const uploadToPinata = async (file) => {
        const formData = new FormData();
        formData.append("file", file);

        const pinataApiKey = process.env.REACT_APP_PINATA_API_KEY;
        const pinataSecretApiKey = process.env.REACT_APP_PINATA_SECRET_API_KEY;

        try {
            const response = await axios.post(
                'https://api.pinata.cloud/pinning/pinFileToIPFS',
                formData,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                        'pinata_api_key': pinataApiKey,
                        'pinata_secret_api_key': pinataSecretApiKey,
                    }
                }
            );
            return response.data.IpfsHash;
        } catch (error) {
            console.error('Error uploading file to Pinata:', error);
            return null;
        }
    };

    const sendMessage = async () => {
        const web3 = await getWeb3();
        const accounts = await web3.eth.getAccounts();
        const contract = await getContract(web3);

        let imageHash = '';
        if (file) {
            imageHash = await uploadToPinata(file);
        }

        try {
            await contract.methods.sendMessage(recipient, message, imageHash).send({ from: accounts[0] });
            setStatus('Message sent successfully!');
        } catch (error) {
            console.error(error);
            setStatus('Failed to send message');
        }
    };

    return (
        <Box>
            <Typography variant="h5" gutterBottom>Send Message</Typography>
            <TextField
                label="Recipient Address"
                variant="outlined"
                fullWidth
                value={recipient}
                onChange={(e) => setRecipient(e.target.value)}
                margin="normal"
            />
            <TextField
                label="Message"
                variant="outlined"
                fullWidth
                multiline
                rows={4}
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                margin="normal"
            />
            <Button
                variant="contained"
                component="label"
                color="primary"
            >
                Attach File
                <input
                    type="file"
                    hidden
                    onChange={(e) => setFile(e.target.files[0])}
                />
            </Button>
            <Button
                variant="contained"
                color="primary"
                onClick={sendMessage}
                style={{ marginTop: '10px' }}
            >
                Send
            </Button>
            {status && (
                <Typography variant="body2" color="textPrimary" gutterBottom>
                    {status}
                </Typography>
            )}
        </Box>
    );
};

export default SendMessage;
