import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Card, CardContent, CardActions } from '@mui/material';
import { getWeb3, getContract } from '../utils/contract';

const SendMessage = () => {
    const [recipient, setRecipient] = useState('');
    const [message, setMessage] = useState('');
    const [status, setStatus] = useState('');

    const sendMessage = async () => {
        const web3 = await getWeb3();
        const accounts = await web3.eth.getAccounts();
        const contract = await getContract(web3);

        try {
            await contract.methods.sendMessage(recipient, message).send({ from: accounts[0] });
            setStatus('Message sent successfully!');
        } catch (error) {
            console.error(error);
            setStatus('Failed to send message');
        }
    };

    return (
        <Card>
            <CardContent>
                <Typography variant="h6" gutterBottom>
                    Send Message
                </Typography>
                <TextField
                    fullWidth
                    label="Recipient Address"
                    value={recipient}
                    onChange={(e) => setRecipient(e.target.value)}
                    margin="normal"
                    variant="outlined"
                />
                <TextField
                    fullWidth
                    label="Message"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    multiline
                    rows={4}
                    margin="normal"
                    variant="outlined"
                />
            </CardContent>
            <CardActions>
                <Button variant="contained" color="primary" onClick={sendMessage}>
                    Send
                </Button>
            </CardActions>
            {status && (
                <Box p={2}>
                    <Typography variant="body2" color="textSecondary">
                        {status}
                    </Typography>
                </Box>
            )}
        </Card>
    );
};

export default SendMessage;
