import React, { useEffect, useState } from 'react';
import { List, ListItem, ListItemText, Typography, Box } from '@mui/material';
import { getWeb3, getContract } from '../utils/contract';

const Inbox = () => {
    const [messages, setMessages] = useState([]);
    const [status, setStatus] = useState('');

    useEffect(() => {
        const fetchMessages = async () => {
            const web3 = await getWeb3();
            const accounts = await web3.eth.getAccounts();
            const contract = await getContract(web3);

            try {
                const userMessages = await contract.methods.getUserMessages(accounts[0]).call();
                setMessages(userMessages);
                setStatus('');
            } catch (error) {
                console.error(error);
                setStatus('Failed to fetch messages');
            }
        };

        fetchMessages();
    }, []);

    return (
        <Box>
            <Typography variant="h6" gutterBottom>
                Inbox
            </Typography>
            {status && (
                <Typography variant="body2" color="textSecondary" style={{ marginBottom: '10px' }}>
                    {status}
                </Typography>
            )}
            <List>
                {messages.map((msg, index) => (
                    <ListItem key={index} divider>
                        <ListItemText
                            primary={`From: ${msg.sender}`}
                            secondary={`Message: ${msg.content}`}
                        />
                    </ListItem>
                ))}
            </List>
        </Box>
    );
};

export default Inbox;
