import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { List, ListItem, ListItemText, Typography, Card, CardContent, Box, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import '../styles/Inbox.css'; // Correct path
import { getWeb3, getContract } from '../utils/contract';

const Inbox = () => {
    const [messages, setMessages] = useState([]);
    const [status, setStatus] = useState('');

    useEffect(() => {
        const fetchMessages = async () => {
            const web3 = await getWeb3();
            const accounts = await web3.eth.getAccounts();
            const contract = await getContract(web3);

            try {
                const userMessageIds = await contract.methods.getUserMessages(accounts[0]).call();
                const userMessages = await Promise.all(
                    userMessageIds.map(id => contract.methods.messages(id).call())
                );
                console.log('Fetched messages:', userMessages);
                setMessages(userMessages);
                setStatus('');
            } catch (error) {
                console.error('Error fetching messages:', error);
                setStatus('Failed to fetch messages');
            }
        };

        fetchMessages();
    }, []);

    const deleteMessage = async (id) => {
        const web3 = await getWeb3();
        const accounts = await web3.eth.getAccounts();
        const contract = await getContract(web3);

        try {
            await contract.methods.deleteMessage(id).send({ from: accounts[0] });
            setMessages(messages.filter((_, index) => index !== id));
        } catch (error) {
            console.error('Error deleting message:', error);
        }
    };

    return (
        <Card className="inbox-card">
            <CardContent>
                <Typography variant="h5" gutterBottom>Inbox</Typography>
                {status && (
                    <Typography variant="body2" color="error" gutterBottom>
                        {status}
                    </Typography>
                )}
                <List className="message-list">
                    {messages.map((msg, index) => (
                        <ListItem key={index} divider className="message-list-item">
                            <ListItemText
                                primary={`From: ${msg.sender}`}
                                secondary={
                                    <>
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            <Link to={`/message/${index}`} className="message-content">
                                                {msg.content}
                                            </Link>
                                        </Typography>
                                        {msg.imageHash && (
                                            <Box className="message-image-container">
                                                <img
                                                    src={`https://gateway.pinata.cloud/ipfs/${msg.imageHash}`}
                                                    alt="Attachment"
                                                    className="message-image"
                                                />
                                            </Box>
                                        )}
                                    </>
                                }
                            />
                            <IconButton edge="end" aria-label="delete" onClick={() => deleteMessage(index)}>
                                <DeleteIcon />
                            </IconButton>
                        </ListItem>
                    ))}
                </List>
            </CardContent>
        </Card>
    );
};

export default Inbox;
