import React, { useEffect, useState } from 'react';
import { List, ListItem, ListItemText, Typography, Card, CardContent, Box } from '@mui/material';
import { getWeb3, getContract } from '../utils/contract';

const Inbox = () => {
    const [messages, setMessages] = useState([]);
    const [status, setStatus] = useState('');

    useEffect(() => {
        const fetchMessages = async () => {
            const web3 = await getWeb3();
            const accounts = await web3.eth.getAccounts();
            const contract = await getContract(web3);

            try {
                const userMessageIds = await contract.methods.getUserMessages(accounts[0]).call();
                const userMessages = await Promise.all(
                    userMessageIds.map(id => contract.methods.messages(id).call())
                );
                setMessages(userMessages);
                setStatus('');
            } catch (error) {
                console.error('Error fetching messages:', error);
                setStatus('Failed to fetch messages');
            }
        };

        fetchMessages();
    }, []);

    return (
        <Card sx={{ p: 2, maxWidth: 600, margin: '20px auto' }}>
            <CardContent>
                <Typography variant="h4" gutterBottom>Inbox</Typography>
                {status && (
                    <Typography variant="body2" color="error" gutterBottom>
                        {status}
                    </Typography>
                )}
                <List>
                    {messages.map((msg, index) => (
                        <ListItem key={index} divider>
                            <ListItemText
                                primary={`From: ${msg.sender}`}
                                secondary={
                                    <>
                                        <Typography component="span" variant="body2" color="textPrimary">
                                            {msg.content}
                                        </Typography>
                                        {msg.imageHash && (
                                            <Box mt={2}>
                                                <img
                                                    src={`https://gateway.pinata.cloud/ipfs/${msg.imageHash}`}
                                                    alt="Attachment"
                                                    style={{ maxWidth: '100%', height: 'auto', marginTop: '10px' }}
                                                />
                                            </Box>
                                        )}
                                    </>
                                }
                            />
                        </ListItem>
                    ))}
                </List>
            </CardContent>
        </Card>
    );
};

export default Inbox;
