import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Container, Grid, Paper, CssBaseline } from '@mui/material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import SendMessage from './components/SendMessage';
import Inbox from './components/Inbox';
import MessageDetail from './components/MessageDetail';
import { getWeb3, getContract } from './utils/contract';

const theme = createTheme({
    palette: {
        mode: 'dark',
        primary: {
            main: '#90caf9',
        },
        background: {
            default: '#121212',
            paper: '#1e1e1e',
        },
    },
    typography: {
        h6: {
            fontWeight: 600,
        },
    },
});

const App = () => {
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        const fetchMessages = async () => {
            const web3 = await getWeb3();
            const accounts = await web3.eth.getAccounts();
            const contract = await getContract(web3);

            try {
                const userMessageIds = await contract.methods.getUserMessages(accounts[0]).call();
                const userMessages = await Promise.all(
                    userMessageIds.map(id => contract.methods.getMessageById(id).call())
                );
                setMessages(userMessages);
            } catch (error) {
                console.error('Error fetching messages:', error);
            }
        };

        fetchMessages();
    }, []);

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline />
            <Router>
                <div className="App">
                    <AppBar position="static">
                        <Toolbar>
                            <Typography variant="h6">
                                Decentralized Mail System
                            </Typography>
                        </Toolbar>
                    </AppBar>
                    <Container>
                        <Grid container spacing={3} style={{ marginTop: '20px' }}>
                            <Grid item xs={12} sm={6}>
                                <Paper style={{ padding: '20px' }}>
                                    <SendMessage />
                                </Paper>
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <Paper style={{ padding: '20px' }}>
                                    <Routes>
                                        <Route path="/" element={<Inbox messages={messages} />} />
                                        <Route path="/message/:id" element={<MessageDetail messages={messages} />} />
                                    </Routes>
                                </Paper>
                            </Grid>
                        </Grid>
                    </Container>
                </div>
            </Router>
        </ThemeProvider>
    );
};

export default App;
