import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getWeb3, getContract } from '../utils/contract';
import '../styles/MessageDetail.css';

const MessageDetail = () => {
    const { id } = useParams();
    const [message, setMessage] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadMessage = async () => {
            const web3 = await getWeb3();
            const contract = await getContract(web3);
            const fetchedMessage = await contract.methods.getMessageById(id).call();
            setMessage(fetchedMessage);
            setLoading(false);
        };

        loadMessage();
    }, [id]);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div className="message-detail">
            <h2>Message Detail</h2>
            <div className="message-detail-content">
                <p>{message.content}</p>
                {message.imageHash && (
                    <div className="message-detail-image-container">
                        <img src={`https://ipfs.io/ipfs/${message.imageHash}`} alt="Message Attachment" className="message-detail-image" />
                    </div>
                )}
            </div>
        </div>
    );
};

export default MessageDetail;
